package com.example.springsecurity.Entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.util.Date;@Data@Entity@AllArgsConstructor@NoArgsConstructor@Table (name = "task")public class Task {    @Id    @GeneratedValue (strategy = GenerationType.IDENTITY)    private Long id;    private String title;    private String description;    private Date date;}