package com.example.springsecurity.controller;import com.example.springsecurity.Entity.Task;import com.example.springsecurity.service.TaskServiceImpl;import lombok.RequiredArgsConstructor;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequiredArgsConstructor@RequestMapping("/api/task")public class TaskController {    @Autowired    private TaskServiceImpl service;    @GetMapping("/")    public ResponseEntity<?> getTasks(){        List<Task> taskList = this.service.getTask();        return ResponseEntity.ok(taskList);    }    @PostMapping("/addTask")    public ResponseEntity<?> createTask(@RequestBody Task task){        Task createTask = service.createTask(task);        return ResponseEntity.status(HttpStatus.CREATED).body(createTask);    }    @PutMapping("/updateTask/{id}")    public ResponseEntity<?> updateTask(@RequestBody Task task, @PathVariable Long id){        Task updateTask = service.updateTask(task, id);        return ResponseEntity.status(HttpStatus.CREATED).body(updateTask);    }    @DeleteMapping("/deleteTask/{id}")    public ResponseEntity<?> deleteTask(@PathVariable Long id){        this.service.deleteTask(id);        return ResponseEntity.ok().build();    }}