package com.example.springsecurity.service;import com.example.springsecurity.Entity.Task;import com.example.springsecurity.repository.TaskRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class TaskServiceImpl implements TaskService{    @Autowired    private TaskRepository taskRepository;    @Override    public List<Task> getTask() {        return (List<Task>) taskRepository.findAll();    }    @Override    public Task createTask(Task task) {        task.setId(task.getId());        return taskRepository.save(task);    }    @Override    public Task updateTask(Task task, Long id) {        task.setId(id);        return taskRepository.save(task);    }    @Override    public void deleteTask(Long id) {        taskRepository.deleteById(id);    }}